import React, {useRef, useState} from 'react';
import Card from './Card';

const Cards = () => {
  const [cards, setCards] = useState (
    [
      {id: 1, status: '', img: '/dogs/01.jpeg'},
      {id: 1, status: '', img: '/dogs/01.jpeg'},
      {id: 2, status: '', img: '/dogs/02.jpeg'},
      {id: 2, status: '', img: '/dogs/02.jpeg'},
      {id: 3, status: '', img: '/dogs/03.jpeg'},
      {id: 3, status: '', img: '/dogs/03.jpeg'},
      {id: 4, status: '', img: '/dogs/04.jpeg'},
      {id: 4, status: '', img: '/dogs/04.jpeg'},
      {id: 5, status: '', img: '/dogs/05.jpeg'},
      {id: 5, status: '', img: '/dogs/05.jpeg'},
      {id: 6, status: '', img: '/dogs/06.jpeg'},
      {id: 6, status: '', img: '/dogs/06.jpeg'},
      {id: 7, status: '', img: '/dogs/07.jpeg'},
      {id: 7, status: '', img: '/dogs/07.jpeg'},
      {id: 8, status: '', img: '/dogs/08.jpeg'},
      {id: 8, status: '', img: '/dogs/08.jpeg'},
    ].sort (() => Math.random () - 0.5)
  );

  const [previousCardState, setPreviousCardState] = useState (-1);
  const previousIndex = useRef (-1);

  const clickhandler = index => {
    console.log ('clicked card', index);
    alert (index);

    if (index !== previousIndex.current) {
      console.log ('GAME ON');
      if (cards[index].status === 'active matched') {
        alert ('already matched!');
      } else {
        if (previousCardState === -1) {
          previousIndex.current = index;
          cards[index].status = 'active';
          setCards ([...cards]);
          setPreviousCardState (index);
        } else {
          previousIndex.current = index;
        }
      }
    } else {
      alert ('CARD CURRENTLY SELECTED!');
    }
  };
  return (
    <div className="container">
      {cards.map ((card, index) => {
        return (
          <Card
            card={card}
            index={index}
            key={index}
            clickhandler={clickhandler}
          />
        );
      })}
    </div>
  );
};

export default Cards;
